class PostgreSqlTest extends \ntentan\test_cases\SqlDatabaseTestCase
{
    protected function setUp()
    {
        \ntentan\Ntentan::$dbConfigFile = 'tests/config/postgresql_db.php';
        parent::setUp();
    }

    protected function getConnection()
    {
        $pdo = new PDO('pgsql:host=localhost;dbname=ntentan_test', 'postgres', 'hello');
        return $this->createDefaultDBConnection($pdo);//, 'test');
    }
    
    protected function getInstance()
    {
        $parameters['hostname'] = 'localhost';
        $parameters['username'] = 'postgres';
        $parameters['password'] = 'hello';
        $parameters['database'] = 'ntentan_test';
        return new \ntentan\models\datastores\Mysql($parameters);
    }
}
